settings.css:
body { background-color: rgba(255, 255, 255, 0.9); overflow: hidden; font-size: 24px; }
.fonts-loaded body { font-family: Corbel; }
.trpContentPanel { background-color: transparent; }
.scroller::-webkit-scrollbar { width: 0; }
.chat-item { margin: 0px; padding: 2px 6px; display: none; }
.chat-item__details { display: none; }
.chat-item__container { margin: 0; padding: 0; }
.chat-item__content { margin: 0; padding: 0; }
.chat-item__aside { display: none; }
.chat-and-toolbar-wrapper { padding: 0; }
.right-toolbar-region { display: none; }
.chat-header { display: none; }
.chat-input { display: none; }
.chat-item:last-child { display: block; }
.chat-item.unread .chat-item__container { border-right:none; background-color:rgba(0,0,0,0); }
.chat-item.unread.mentioned .chat-item__container { border-right:none; background-color:rgba(0,0,0,0); }

body { background-color: rgba(255, 255, 255, 0.9); overflow: hidden; font-size: 24px; }
